import React from 'react';
import { Link } from 'react-router-dom';
import { formatDistanceToNow, isPast, format } from 'date-fns';
import { pt } from 'date-fns/locale';

const CounterCard = ({ counter, onDelete, onToggleFavorite }) => {
  const { _id, name, description, eventDate, category, isFavorite } = counter;

  const eventDateObj = new Date(eventDate);
  const isPastEvent = isPast(eventDateObj);

  // Calcular a distância de tempo
  const timeDistance = formatDistanceToNow(eventDateObj, { 
    addSuffix: true,
    locale: pt 
  });

  // Formatar a data do evento
  const formattedDate = format(eventDateObj, "dd 'de' MMMM 'de' yyyy", { locale: pt });

  // Determinar a classe CSS com base no status do evento
  const cardClass = isPastEvent 
    ? 'counter-card-past' 
    : isFavorite 
      ? 'counter-card-favorite' 
      : 'counter-card-future';

  return (
    <div className={`counter-card ${cardClass}`}>
      <div className="flex justify-between items-start mb-2">
        <h3 className="text-xl font-bold truncate">{name}</h3>
        <div className="flex space-x-2">
          <button 
            onClick={() => onToggleFavorite(_id, isFavorite)}
            className="text-yellow-500 hover:text-yellow-600"
            title={isFavorite ? "Remover dos favoritos" : "Adicionar aos favoritos"}
          >
            {isFavorite ? (
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" viewBox="0 0 20 20" fill="currentColor">
                <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
              </svg>
            ) : (
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11.049 2.927c.3-.921 1.603-.921 1.902 0l1.519 4.674a1 1 0 00.95.69h4.915c.969 0 1.371 1.24.588 1.81l-3.976 2.888a1 1 0 00-.363 1.118l1.518 4.674c.3.922-.755 1.688-1.538 1.118l-3.976-2.888a1 1 0 00-1.176 0l-3.976 2.888c-.783.57-1.838-.197-1.538-1.118l1.518-4.674a1 1 0 00-.363-1.118l-3.976-2.888c-.784-.57-.38-1.81.588-1.81h4.914a1 1 0 00.951-.69l1.519-4.674z" />
              </svg>
            )}
          </button>
        </div>
      </div>

      <div className="mb-4">
        <span className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2">
          {category}
        </span>
      </div>

      {description && (
        <p className="text-gray-700 mb-4 line-clamp-2">{description}</p>
      )}

      <div className="mb-4">
        <p className="text-lg font-bold">
          {isPastEvent ? 'Ocorreu' : 'Ocorre'} {timeDistance}
        </p>
        <p className="text-sm text-gray-600">{formattedDate}</p>
      </div>

      <div className="flex justify-between mt-auto">
        <Link 
          to={`/counter/${_id}`} 
          className="text-blue-600 hover:text-blue-800"
        >
          Ver detalhes
        </Link>

        <button 
          onClick={() => onDelete(_id)}
          className="text-red-600 hover:text-red-800"
          title="Excluir contador"
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
            <path fillRule="evenodd" d="M9 2a1 1 0 00-.894.553L7.382 4H4a1 1 0 000 2v10a2 2 0 002 2h8a2 2 0 002-2V6a1 1 0 100-2h-3.382l-.724-1.447A1 1 0 0011 2H9zM7 8a1 1 0 012 0v6a1 1 0 11-2 0V8zm5-1a1 1 0 00-1 1v6a1 1 0 102 0V8a1 1 0 00-1-1z" clipRule="evenodd" />
          </svg>
        </button>
      </div>
    </div>
  );
};

export default CounterCard;
